// Extract dates from user message using NLP
const userMessage = event.payload.text.toLowerCase();
const dateRegex = /\b(\d{1,2}[\/-]\d{1,2}[\/-]\d{2,4}|\d{4}-\d{2}-\d{2})\b/g;
const dates = userMessage.match(dateRegex) || [];

// Extract number of guests
const guestRegex = /\b(\d+)\s*(guest|person|people|adult)s?\b/i;
const guestMatch = userMessage.match(guestRegex);
const guests = guestMatch ? parseInt(guestMatch[1]) : 1;

// Set workflow variables
workflow.check_in_date = dates[0] || '';
workflow.check_out_date = dates[1] || '';
workflow.guests = guests;

// If dates are missing, ask user
if (!workflow.check_in_date || !workflow.check_out_date) {
  workflow.missing_info = true;
} else {
  workflow.missing_info = false;
}

const axios = require('axios');

// CloudBeds API configuration
const API_BASE_URL = 'https://hotels.cloudbeds.com/api/v1.1';
const API_KEY = workflow.cloudbed_api_key || process.env.CLOUDBED_API_KEY;
const PROPERTY_ID = workflow.cloudbed_property_id || process.env.CLOUDBED_PROPERTY_ID;

if (!API_KEY || !PROPERTY_ID) {
  workflow.error = 'CloudBeds API credentials not configured';
  return;
}

try {
  // Format dates for API
  const checkIn = new Date(workflow.check_in_date).toISOString().split('T')[0];
  const checkOut = new Date(workflow.check_out_date).toISOString().split('T')[0];

  // Call CloudBeds availability API
  const response = await axios.get(`${API_BASE_URL}/getAvailability`, {
    headers: {
      'Authorization': `Bearer ${API_KEY}`,
      'Content-Type': 'application/json'
    },
    params: {
      propertyID: PROPERTY_ID,
      startDate: checkIn,
      endDate: checkOut,
      adults: workflow.guests,
      children: 0
    }
  });

  workflow.availability_data = response.data;
  workflow.api_success = true;

} catch (error) {
  console.error('CloudBeds API Error:', error.response?.data || error.message);
  workflow.error = error.response?.data?.message || 'Failed to check availability';
  workflow.api_success = false;
}

const data = workflow.availability_data;

if (!data || !data.data || !data.data.roomTypes) {
  workflow.formatted_response = '❌ No availability data received from CloudBeds.';
  return;
}

const roomTypes = data.data.roomTypes;
const availableRooms = roomTypes.filter(room => room.available > 0);

if (availableRooms.length === 0) {
  workflow.formatted_response = `😔 Sorry, no rooms are available from ${workflow.check_in_date} to ${workflow.check_out_date} for ${workflow.guests} guest(s).\n\nWould you like to try different dates?`;
  return;
}

// Format available rooms
let response = `🏨 **Available Rooms** (${workflow.check_in_date} to ${workflow.check_out_date})\n\n`;

availableRooms.forEach(room => {
  response += `🛏️ **${room.roomTypeName}**\n`;
  response += `   💰 Price: $${room.averagePrice}/night\n`;
  response += `   🏠 Available: ${room.available} room(s)\n`;
  response += `   👥 Max occupancy: ${room.maxOccupancy} guests\n\n`;
});

response += '💬 Would you like to check different dates or need more information?';

workflow.formatted_response = response;

