// Extract dates from user message using NLP
const userMessage = event.payload.text.toLowerCase();
const dateRegex = /\b(\d{1,2}[\/-]\d{1,2}[\/-]\d{2,4}|\d{4}-\d{2}-\d{2})\b/g;
const dates = userMessage.match(dateRegex) || [];

// Extract number of guests
const guestRegex = /\b(\d+)\s*(guest|person|people|adult)s?\b/i;
const guestMatch = userMessage.match(guestRegex);
const guests = guestMatch ? parseInt(guestMatch[1]) : 1;

// Set workflow variables
workflow.check_in_date = dates[0] || '';
workflow.check_out_date = dates[1] || '';
workflow.guests = guests;

// If dates are missing, ask user
if (!workflow.check_in_date || !workflow.check_out_date) {
  workflow.missing_info = true;
} else {
  workflow.missing_info = false;
}

// Mock availability data for testing
const mockRooms = [
  {
    roomTypeName: 'Ocean View Suite',
    averagePrice: 450,
    available: 2,
    maxOccupancy: 4
  },
  {
    roomTypeName: 'Garden Villa',
    averagePrice: 350,
    available: 1,
    maxOccupancy: 2
  },
  {
    roomTypeName: 'Master Pool Suite',
    averagePrice: 650,
    available: 1,
    maxOccupancy: 6
  }
];

// Filter rooms based on guest count
const availableRooms = mockRooms.filter(room => room.maxOccupancy >= workflow.guests);

workflow.availability_data = {
  data: {
    roomTypes: availableRooms
  }
};
workflow.api_success = true;

const data = workflow.availability_data;

if (!data || !data.data || !data.data.roomTypes) {
  workflow.formatted_response = 'âŒ No availability data received.';
  return;
}

const roomTypes = data.data.roomTypes;
const availableRooms = roomTypes.filter(room => room.available > 0);

if (availableRooms.length === 0) {
  workflow.formatted_response = `ğŸ˜” Sorry, no rooms are available from ${workflow.check_in_date} to ${workflow.check_out_date} for ${workflow.guests} guest(s).\n\nWould you like to try different dates?`;
  return;
}

// Format available rooms
let response = `ğŸ¨ **Available Rooms** (${workflow.check_in_date} to ${workflow.check_out_date})\n\n`;
response += `*This is a test version with mock data*\n\n`;

availableRooms.forEach(room => {
  response += `ğŸ›ï¸ **${room.roomTypeName}**\n`;
  response += `   ğŸ’° Price: $${room.averagePrice}/night\n`;
  response += `   ğŸ  Available: ${room.available} room(s)\n`;
  response += `   ğŸ‘¥ Max occupancy: ${room.maxOccupancy} guests\n\n`;
